import React from 'react'
import {Form,Input} from 'antd'
import TextArea from 'antd/es/input/TextArea'

function Hospital({type}) {
  return (
    <>
       <Form.Item
       label={type=== 'hospital' ? 'Hospital Name':'Organization Name'}
       name={type=== 'hospital' ? 'Hospital Name':'Organization Name'}
       normalize={(value) => value.trim()}
       rules={[
         { required: true, message:`please enter your ${type} name` },
         { min: 3 },
       ]}
       hasFeedback
       >
        <Input/>
       </Form.Item>
       <Form.Item label='owner' name='Owner'
        normalize={(value) => value.trim()}
        rules={[
          { required: true, message: "please enter your owner name" },
          { min: 3 },
        ]}
        hasFeedback
        >
        <Input/>
       </Form.Item>
       <Form.Item label='email' name='Email' normalize={(value) => value.trim()}
            rules={[
              { required: true, message: "please enter your email Id" },
              { min: 8 },
              {type:'email'}
            ]}
            hasFeedback
            >
        <Input/>
       </Form.Item>
       <Form.Item label='phone' name='Phone' 
            normalize={(value) => value.trim()}
             rules={[
               { required: true, message: "please enter your contact number" },
               { min: 10 },
               
             ]}
             hasFeedback
             >
        <Input/>
       </Form.Item>
       <Form.Item label='website' name='Website'
          normalize={(value) => value.trim()}
          rules={[
            { required: true, message: "please enter your website" },
            { min: 8 },
            {type:"email"}
            
          ]}
          hasFeedback
          >
        <Input/>
       </Form.Item>
       <Form.Item label='password' name='Password'
       normalize={(value) => value.trim()}
       rules={[
         { required: true, message: "please enter your password" },
         { min: 8 },
         {max:15},
         
       ]}
       hasFeedback
       >
        <Input/>
       </Form.Item>
       <Form.Item label='address' name='Address' className='col-span-2'
      
       rules={[
         { required: true, message: "please enter your address" },
         { min: 20 },
         {max:50},
         {whitespace:true}
         
       ]}
       hasFeedback
       >
        <TextArea/>
       </Form.Item>
       
    </>
  )
}

export default Hospital
