import React, { useState } from "react";
import { Button, Form, Input, Radio } from "antd";
import { Link } from "react-router-dom";


function Login() {
  const [type, settype] = useState("donar");
  const [form] = Form.useForm();

  const onFinish = (values) => {
    console.log("form datas", values);
  };
  const onFinishFailed = (errorInfo) => {
    // Handle form validation failures
    console.log("Failed:", errorInfo);
  };

  return (
    <div className="flex h-screen items-center justify-center  bg-primary">
      <Form
        autoComplete="off"
        onFinish={onFinish}
        form={form}
        onFinishFailed={onFinishFailed}
        layout="vertical"
        className="bg-white rounded shadow grid p-4 gap-3 w-1/3"
      >
        <h1 className="uppercase text-xl  text-white text-red-600">
        RED CROSS  <span className="text-l ms-2  bg-red-500">{type.toUpperCase()} - Login </span>
          <hr />
        </h1>
        <Radio.Group
          
          onChange={(e) => settype(e.target.value)}
          value={type}
        >
          <Radio value="donar">Donar</Radio>
          <Radio value="hospital">Hospital</Radio>
          <Radio value="organization">Organization</Radio>
        </Radio.Group>
        
          <>
           
            <Form.Item label="Email" 
            name="email"
            normalize={(value) => value.trim()}
            rules={[
              { required: true, message: "please enter your email Id" },
              { min: 8 },
              {type:'email'}
            ]}
            hasFeedback>
              <Input />
            </Form.Item>
       
            <Form.Item label="Password" 
            name="password"
            normalize={(value) => value.trim()}
             rules={[
               { required: true, message: "please enter your password" },
               { min: 8 },
               {max:15},
               
             ]}
             hasFeedback>
              <Input type="password" />
            </Form.Item>
          </>
        
     
        <Button htmlType="submit" type="primary" block >
        Login
        </Button>
        <Link
          to="/register"
          className=" text-center text-gray-700 underline"
        >
         Don't have an account
        </Link>
      </Form>
    </div>
  );
}

export default Login;
