import React, { useState } from "react";
import { Button, Form, Input, Radio } from "antd";
import { Link } from "react-router-dom";
import Hospital from "./Hospital";

function Register() {
  const [type, settype] = useState("donar");
  const onFinish =(values)=>{
    console.log("form datas",values);
  }
  const onFinishFailed = (errorInfo) => {
    // Handle form validation failures
    console.log('Failed:', errorInfo);
  };
  const noWhitespace = (rule, value, callback) => {
    if (/\s/.test(value)) {
      callback('Spaces are not allowed.');
    } else {
      callback().newpromise();
    }
  };
  return (
    <div className="flex h-screen items-center justify-center bg-primary">
      <Form
        autoComplete='off'
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
        layout="vertical"
        className="bg-white rounded shadow grid grid-col-2 p-5 gap-5 w-1/2"
      >
        <h1 className="col-span-2 uppercase text-xl text-red-600">
          {type.toUpperCase()} - Register
          <hr />
        </h1>
        <Radio.Group
          className="col-span-2"
          onChange={(e) => settype(e.target.value)}
          value={type}
        >
          <Radio value="donar">Donar</Radio>
          <Radio value="hospital">Hospital</Radio>
          <Radio value="organization">Organization</Radio>
        </Radio.Group>
        {type === "donar" && (
          <>
            <Form.Item label="Name" name='name' rules={[{required:true,message:'please enter your name',},  {
            validator: noWhitespace, // Use the custom validation rule
          },,{min:3}]}>
              <Input />
            </Form.Item>
            <Form.Item label="Email" name='email'>
              <Input />
            </Form.Item>
            <Form.Item label="Phone" name='phone'>
              <Input />
            </Form.Item>
            <Form.Item label="Password" name='password'>
              <Input type="password" />
            </Form.Item>
          </>
        )}
        {type!== "donar" &&
        <>
        <Hospital type={type}/>
        </>}
        <Button htmlType='submit' type="primary" block className="col-span-2">
          Register
        </Button>
        <Link
          to="/login"
          className="col-span-2 text-center text-gray-700 underline"
        >
          Already have an account
        </Link>
      </Form>
    </div>
  );
}

export default Register;
