import React, { useState } from "react";
import { Button, Form, Input, Radio } from "antd";
import { Link } from "react-router-dom";
import Hospital from "./Hospital";

function Register() {
  const [type, settype] = useState("donar");
  const [form] = Form.useForm();

  const onFinish = (values) => {
    console.log("form datas", values);
  };
  const onFinishFailed = (errorInfo) => {
    // Handle form validation failures
    console.log("Failed:", errorInfo);
  };
  const divStyle = {
    backgroundImage: 'url("../Register/pages/assets/glossy1.jpg")', // Replace with the path to your image
    backgroundSize: 'cover', // You can adjust this based on your image and layout requirements
    backgroundRepeat: 'no-repeat',
    width: '100px', // Set the width and height as needed
    height: '100px',
  };
  return (
    <div style={divStyle} className="flex h-screen items-center justify-center  bg-primary">
      <Form
        autoComplete="off"
        onFinish={onFinish}
        form={form}
        onFinishFailed={onFinishFailed}
        layout="vertical"
        className="bg-white rounded shadow grid grid-col-2 p-2 gap-3 w-1/2"
      >
        <h2 className="col-span-2 uppercase text-xl text-red-600">
        <span className="text-l ms-2 me-3 text-white  bg-red-500">RED CROSS </span> {type.toUpperCase()} - Register
          <hr />
        </h2>
        <Radio.Group
          className="col-span-2"
          onChange={(e) => settype(e.target.value)}
          value={type}
        >
          <Radio value="donar">Donar</Radio>
          <Radio value="hospital">Hospital</Radio>
          <Radio value="organization">Organization</Radio>
        </Radio.Group>
        {type === "donar" && (
          <>
            <Form.Item
              label="Name"
              name="name"
              normalize={(value) => value.trim()}
              rules={[
                { required: true, message: "please enter your name" },
                { min: 3 },
              ]}
              hasFeedback
            >
              <Input />
            </Form.Item>
            <Form.Item label="Email" 
            name="email"
            normalize={(value) => value.trim()}
            rules={[
              { required: true, message: "please enter your email Id" },
              { min: 8 },
              {type:'email'}
            ]}
            hasFeedback>
              <Input />
            </Form.Item>
            <Form.Item label="Phone"
             name="phone"
             normalize={(value) => value.trim()}
             rules={[
               { required: true, message: "please enter your contact number" },
               { min: 10 },
               
             ]}
             hasFeedback>
             
              <Input />
            </Form.Item>
            <Form.Item label="Password" 
            name="password"
            normalize={(value) => value.trim()}
             rules={[
               { required: true, message: "please enter your password" },
               { min: 8 },
               {max:15},
               
             ]}
             hasFeedback>
              <Input type="password" />
            </Form.Item>
          </>
        )}
        {type !== "donar" && (
          <>
            <Hospital type={type} />
          </>
        )}
        <Button htmlType="submit" type="primary" block className="col-span-2">
          Register
        </Button>
        <Link
          to="/login"
          className="col-span-2 text-center text-gray-700 underline"
        >
          Already have an account
        </Link>
      </Form>
    </div>
  );
}

export default Register;
