import React, { useState } from "react";
import { Button, Form, Input, Radio, message } from "antd";
import { Link } from "react-router-dom";
import Hospital from "./Hospital";
import { RegisterUser } from "../../Apicall/users";
import { getAntdInputValidation } from "../../utils/helper";


function Register() {
  const [type, settype] = useState("donor");
  const [form] = Form.useForm();
  form.setFieldsValue({});
  const onFinish = async(values) => {
    console.log("form datas", values);
    try {
        const response= await RegisterUser({...values, userType:type})
        if(response.success){
        message.success(response.message)
        }
        else {
            throw new Error(response.message)
        }  
    } catch (error) {
        message.error(error.message)
    }
  };
  form.resetFields();

 

  
 
  return (
    <div className="bgc">
      <div className="flex h-screen items-center justify-center bg-primary">
      <Form
        layout="vertical"
        className="bg-white rounded shadow grid grid-cols-2 p-5 gap-5 w-1/2"
        onFinish={onFinish}
      >
        <h1 className="col-span-2 uppercase text-2xl">
          <span className="text-primary">
            {type.toUpperCase()} - REGISTRATION
          </span>
          <hr />
        </h1>

        <Radio.Group
          onChange={(e) => setType(e.target.value)}
          value={type}
          className="col-span-2"
        >
          <Radio value="donor">donor</Radio>
          <Radio value="hospital">Hospital</Radio>
          <Radio value="organization">Organization</Radio>
        </Radio.Group>

        {type === "donor" && (
          <>
            {" "}
            <Form.Item
              label="Name"
              name="name"
              rules={getAntdInputValidation()}
            >
              <Input />
            </Form.Item>
            <Form.Item
              label="Email"
              name="email"
              rules={getAntdInputValidation()}
            >
              <Input />
            </Form.Item>
            <Form.Item
              label="Phone"
              name="phone"
              rules={getAntdInputValidation()}
            >
              <Input />
            </Form.Item>
            <Form.Item
              label="Password"
              name="password"
              rules={getAntdInputValidation()}
            >
              <Input type="password" />
            </Form.Item>
          </>
        )}

        {type !== "donor" && <Hospital type={type}/>}

        <Button type="primary" block className="col-span-2" htmlType="submit">
          Register
        </Button>

        <Link
          to="/login"
          className="col-span-2 text-center text-gray-700 underline"
        >
          Already have an account ? <span style={{color:'red'}}>Login</span>
        </Link>
      </Form>
    </div>
    </div>
  );
}

export default Register;
